import { FunctionComponent, useMemo, type CSSProperties } from "react";
import styles from "./Button.module.css";

export type ButtonType = {
  className?: string;
  leftIcon?: string;
  children?: string;
  rightIcon?: string;

  /** Style props */
  propFontSize?: CSSProperties["fontSize"];
  propDisplay?: CSSProperties["display"];
  propMinWidth?: CSSProperties["minWidth"];
};

const Button: FunctionComponent<ButtonType> = ({
  className = "",
  leftIcon,
  children,
  rightIcon,
  propFontSize,
  propDisplay,
  propMinWidth,
}) => {
  const childrenStyle: CSSProperties = useMemo(() => {
    return {
      fontSize: propFontSize,
      display: propDisplay,
      minWidth: propMinWidth,
    };
  }, [propFontSize, propDisplay, propMinWidth]);

  return (
    <button className={[styles.button, className].join(" ")}>
      <img className={styles.leftIcon} alt="" src={leftIcon} />
      <div className={styles.children} style={childrenStyle}>
        {children}
      </div>
      <img className={styles.rightIcon} alt="" src={rightIcon} />
    </button>
  );
};

export default Button;
