import { FunctionComponent, useMemo, type CSSProperties } from "react";
import styles from "./Button.module.css";

export type ButtonType = {
  className?: string;
  leftIcon?: string;
  children?: string;
  rightIcon?: string;

  /** Style props */
  propFontSize?: CSSProperties["fontSize"];
  propDisplay?: CSSProperties["display"];
  propMinWidth?: CSSProperties["minWidth"];
  buttonPosition?: CSSProperties["position"];
  buttonTop?: CSSProperties["top"];
  buttonLeft?: CSSProperties["left"];
  buttonBorder?: CSSProperties["border"];
  buttonFlex?: CSSProperties["flex"];
};

const Button: FunctionComponent<ButtonType> = ({
  className = "",
  leftIcon,
  children,
  propFontSize,
  propDisplay,
  propMinWidth,
  rightIcon,
  buttonPosition,
  buttonTop,
  buttonLeft,
  buttonBorder,
  buttonFlex,
}) => {
  const childrenStyle: CSSProperties = useMemo(() => {
    return {
      fontSize: propFontSize,
      display: propDisplay,
      minWidth: propMinWidth,
    };
  }, [propFontSize, propDisplay, propMinWidth]);

  const buttonStyle: CSSProperties = useMemo(() => {
    return {
      position: buttonPosition,
      top: buttonTop,
      left: buttonLeft,
      border: buttonBorder,
      flex: buttonFlex,
    };
  }, [buttonPosition, buttonTop, buttonLeft, buttonBorder, buttonFlex]);

  return (
    <button
      className={[styles.button, className].join(" ")}
      style={buttonStyle}
    >
      <img className={styles.leftIcon} alt="" src={leftIcon} />
      <div className={styles.children} style={childrenStyle}>
        {children}
      </div>
      <img className={styles.rightIcon} alt="" src={rightIcon} />
    </button>
  );
};

export default Button;
